#include &lt;cmath&gt;#include &lt;cstdio&gt;#include &lt;vector&gt;#include &lt;cctype&gt;#define ll long longll read(){    ll x = 0, f = 1; char ch = getchar();    for(; !isdigit(ch); ch = getchar()) f ^= (ch == '-');    for(; isdigit(ch); ch = getchar()) x = (x &lt;&lt; 3) + (x &lt;&lt; 1) + (ch xor 48);    return f ? x : -x;}ll min(ll a, ll b){return a &lt; b ? a : b;}void solve(){    int n = read();                 // 区间长度    int B = sqrt(n);                // 大块长度    int N = (n - 1) / B + 1;        // 大块个数    std::vector&lt;ll&gt; a(n+1);         // 序列值    std::vector&lt;int&gt; id(n+1);       // id[x]: 第x个数属于第几个大块    std::vector&lt;int&gt; L(N+1), R(N+1);// 第k个大块的区间左右边界    for(int i = 1; i &lt;= n; ++i){        a[i] = read();              // 读入初始序列        id[i] = (i - 1) / B + 1;    // 给每个大块编号, 自行推导为什么是 (i-1)/B+1    }    for(int i = 1; i &lt;= N; ++i){    // 给每个大块设置边界        L[i] = (i - 1) * B + 1;        R[i] = min(i * B, n);       // 最后一个块的右边界不能超过n    }    auto modify = [&amp;](int l, int r, ll c) -&gt; void {        if(id[l] == id[r]){ // 在同一个大块中, 直接暴力修改            for(int i = l; i &lt;= r; ++i) /*对a[i]暴力修改*/            return;        }        // 否则, 就拆分成左散块+右散块+中间大块        // 左散块的区间是 [l, R[id[l]]], 右散块的范围是 [L[id[r]], r]        // 中间大块的编号范围是 (id[l], id[r])                // 对左右散块操作        for(int i = l; i &lt;= R[id[l]]; ++i) /*对a[i]暴力修改*/        for(int i = L[id[r]]; i &lt;= r; ++i) /*对a[i]暴力修改*/                // 对大块操作        for(int i = id[l]+1; i &lt; id[r]; ++i) /*对第i个大块做修改*/        return;    };    auto query = [&amp;](int l, int r) -&gt; ll {        ll ans = 0; // 分类方式同modify        if(id[l] == id[r]){            for(int i = l; i &lt;= r; ++i) /*对下标暴力统计*/            return ans;        }        for(int i = l; i &lt;= R[id[l]]; ++i)    /*对下标暴力统计*/        for(int i = L[id[r]]; i &lt;= r; ++i)    /*对下标暴力统计*/        for(int i = id[l]+1; i &lt; id[r]; ++i)  /*统计各个大块*/        return ans;    };    int q = read();    while(q--){        // 假定 op = 0: 修改区间 [l, r]; op = 1: 查询区间 [l, r]        int op = read(), l = read(), r = read();        if(!op) modify(l, r, read());        else printf("%lld\n", query(l, r));    }}int main(){    solve();}


