      #include &lt;map&gt;#include &lt;queue&gt;#include &lt;cmath&gt;#include &lt;cstdio&gt;#include &lt;vector&gt;#include &lt;string&gt;#include &lt;iostream&gt;#include &lt;algorithm&gt;#include &lt;ext/rope&gt;#define ll long long#define mod 10007ll read(){    ll x = 0, f = 1; char ch = getchar();    for(; !isdigit(ch); ch = getchar()) f ^= (ch == '-');    for(; isdigit(ch); ch = getchar()) x = (x &lt;&lt; 3) + (x &lt;&lt; 1) + (ch ^ 48);    return f ? x : -x;}ll min(ll a, ll b){return a &lt; b? a : b;}ll max(ll a, ll b){return a &gt; b? a : b;}ll gcd(ll a, ll b){return b ? gcd(b, a % b) : a;}void solve(){    int n = read();    int B = sqrt(n);    int N = (n - 1) / B + 1;    std::vector&lt;ll&gt; a(n+1);    std::vector&lt;int&gt; id(n+1);    std::vector&lt;int&gt; L(N+1), R(N+1);    std::vector&lt;ll&gt; add(N+1), mul(N+1, 1);    for(int i = 1; i &lt;= N; ++i){        L[i] = (i - 1) * B + 1;        R[i] = min(i * B, n);    }    for(int i = 1; i &lt;= n; ++i){        a[i] = read();        id[i] = (i - 1) / B + 1;    }    auto Pushdown = [&amp;](int x) -&gt; void {        for(int i = L[x]; i &lt;= R[x]; ++i){            a[i] = (a[i] * mul[id[i]]) % mod + add[id[i]];            a[i] %= mod;        }        mul[x] = 1; add[x] = 0;    };    auto Add = [&amp;](int l, int r, ll c) -&gt; void {        if(id[l] == id[r]){            Pushdown(id[l]);            for(int i = l; i &lt;= r; ++i) a[i] += c, a[i] %= mod;            return;        }        Pushdown(id[l]); Pushdown(id[r]);        for(int i = l; i &lt;= R[id[l]]; ++i) a[i] += c, a[i] %= mod;        for(int i = id[l] + 1; i &lt; id[r]; ++i) add[i] += c, add[i] %= mod;        for(int i = L[id[r]]; i &lt;= r; ++i) a[i] += c, a[i] %= mod;    };    auto Mul = [&amp;](int l, int r, ll c) -&gt; void {        if(id[l] == id[r]){            Pushdown(id[l]);            for(int i = l; i &lt;= r; ++i) a[i] *= c, a[i] %= mod;            return;        }        Pushdown(id[l]); Pushdown(id[r]);        for(int i = l; i &lt;= R[id[l]]; ++i) a[i] *= c, a[i] %= mod;        for(int i = id[l] + 1; i &lt; id[r]; ++i) mul[i] *= c, add[i] *= c, mul[i] %= mod, add[i] %= mod;        for(int i = L[id[r]]; i &lt;= r; ++i) a[i] *= c, a[i] %= mod;    };    auto query = [&amp;](int x) -&gt; ll {        ll res = ((a[x] * mul[id[x]]) % mod + add[id[x]]) % mod;        return (res + mod) % mod;    };    for(int i = 1; i &lt;= n; ++i){        int opt = read(), l = read(), r = read(); ll c = read();        if(opt == 0) Add(l, r, c);        else if(opt == 1) Mul(l, r, c);        else printf("%lld\n", query(r));    //    for(int i = 1; i &lt;= n; ++i) printf("%lld ", a[i]); puts("");    }}int main(){//    freopen("input.txt", "r", stdin);    int t = 1;    while(t--) solve();}





